var i,content="";for(obj=inputstream,i=0;i<obj.length;i++)title=obj[i].title,city=obj[i].city,country=obj[i].country,company=obj[i].company,department=obj[i].department,time=obj[i].time,link=obj[i].link,content+="<div class='searchable-item card'><h3>"+title+"</h3><br><h4>"+city+", "+country+"</h4><br><p>"+company+"</p><p>"+department+"</p><br><i class='fa fa-clock-o'></i> "+time+"<br><a class='btn btn-default' href='"+link+"'>Read more <i class='fa fa-arrow-right'></i></a><br></div>";document.getElementById("content").innerHTML=content;var qsRegex,grid=document.querySelector(".grid"),iso=new Isotope(grid,{itemSelector:".searchable-item",layoutMode:"masonry"}),quicksearch=document.getElementsByClassName("quicksearch")[0];quicksearch.onkeyup=function(){qsRegex=new RegExp(quicksearch.value,"gi"),filterValue=function(e){var t=e.textContent;return t.match(qsRegex)},iso.arrange({filter:filterValue})};